mapper.select:
select username, userid
from member
where userid = #{userid} and userpwd = #{userpwd}

select username, userid, joindate
from member
where userid = #{userid}

mapper.update:
update member
set token = #{token}
where userid = #{userid}

mapper.select:
select token
from member
where userid = #{userid}

mapper.update:
update member
set token = #{token, jdbcType=VARCHAR}
where userid = #{userid}
