mapper.select:
select name, id
from member
where id = #{id} and pwd = #{pwd}

select name, id, joindate
from member
where id = #{id}

mapper.update:
update member
set token = #{token}
where id = #{id}

mapper.select:
select token
from member
where id = #{id}

mapper.update:
update member
set token = #{token, jdbcType=VARCHAR}
where id = #{id}
